  var extensiones = arguments.length > 1 && arguments[1] !== undefined ? arguments[1] : ['jpg', 'png', 'gif', 'jpeg', 'bmp', 'svg'];
  var pathUbication = arguments.length > 2 && arguments[2] !== undefined ? arguments[2] : '';
