    var extension = nombre_extension[nombre_extension.length - 1];

    if (!extensiones.includes(extension)) {
      return reject({
        mgs: "la extensiones permitidas son: ".concat(extensiones)
      });
    }

    var nombreTemporal = uuidv4() + '.' + extension;
    var uploadPath = path.join(__dirname, '../uploads/', pathUbication, nombreTemporal);
    archivo.mv(uploadPath, function (err) {
      if (err) {
        reject(err);
      }

      resolve(nombreTemporal);
    });
  });
